<div class="ml4 thrust-table-density">
    <a id='collapse' class="mr1 pointer o30"    onclick="$('table').removeClass('expanded').addClass('condensed');    $(this).removeClass('o30'); $('#expand').addClass('o30'); $('#expand').addClass('o30');">@icon(bars)</a>
    <a id='normal'   class="mr1 pointer"        onclick="$('table').removeClass('condensed expanded'); $(this).removeClass('o30'); $('#collapse').addClass('o30'); $('#expand').addClass('o30');">@icon(list)</a>
    <a id='expand'   class="pointer o30"    onclick="$('table').removeClass('condensed').addClass('expanded'); $(this).removeClass('o30'); $('#collapse').addClass('o30'); $('#normal').addClass('o30');">@icon(th-list)</a>
</div>
